class IpAddress{constructor(){this.address4=/^(\d{1,3}\.){3}\d{1,3}$/,this.address6=/^([\da-f]{1,4}:){7}[\da-f]{1,4}$/i}isAddress4(s){return!!this.address4.test(s)&&s.split(".").every((s=>parseInt(s)<=255))}isAddress6(s){return!!this.address6.test(s)&&s.split(":").every((s=>s.length<=4))}isValid(s){return this.isAddress4(s)||this.isAddress6(s)}}