class IpAddress{constructor(){this.address4=/^(\d{1,3}\.){3}\d{1,3}$/,this.address6=/^([\da-f]{1,4}:){7}[\da-f]{1,4}$/i}isAddress4(s){return!!this.address4.test(s)&&s.split(".").every((s=>parseInt(s)<=255))}isAddress6(s){return!!this.address6.test(this.expandAddress6(s))&&s.split(":").every((s=>s.length<=4))}expandAddress6(s){if(null===s.match(/:/g))return s;if(7===s.match(/:/g).length)return s;var r=0,t=[],d=s.split("::");d.forEach((function(s,t){r+=s.split(":").length})),t.push(d[0]);for(var e=0;e<8-r;e++)t.push("0000");return t.push(d[1]),t.filter((s=>s)).forEach((function(s,r){t[r]=t[r].toString().padStart(4,"0")})),t.join(":")}isValid(s){return this.isAddress4(s)||this.isAddress6(s)}}